/**
 * @file
 * Javascript behaviors for the Shareables module.
 */

/**
 * Click callback function for share modal open and close links.
 *
 * @param {boolean} showHide
 *   True if the modal should be shown, false if it should be hidden.
 * @param {string} shareId
 *   A unique string identifying the current shareable.
 * @param {integer} nid
 *   The node id of the current shareable.
 */
function social_share_modal(showHide, shareId, nid) {
  if (showHide) {
    jQuery("#social-share-modal-" + shareId).show();
    jQuery("#social-share-this-button-wrapper-" + shareId).hide();
    _gaq.push(['_trackEvent', 'Share Button', 'Open', 'node/' + nid]);
  }
  else {
    jQuery("#social-share-modal-" + shareId).hide();
    jQuery("#social-share-this-button-wrapper-" + shareId).show();
    _gaq.push(['_trackEvent', 'Share Button', 'Close', 'node/' + nid]);
  }
}

(function ($) {

  Drupal.behaviors.shareables = {

    attach: function (context, settings) {

      $('article.shareable', context).once('shareables-tracking', function() {
        var nodePath = 'node/' + $(this).attr('id').replace('modal-nid-','');

        // Add Google Analytics event tracking to Social Share links.
        $(this).find('a.social-share-mailto').click(function() {
          _gaq.push(['_trackEvent', 'Social Share Links', 'MailTo', nodePath]);
        });
        $(this).find('a.social-share-facebook').click(function() {
          _gaq.push(['_trackEvent', 'Social Share Links', 'Facebook', nodePath]);
        });
        $(this).find('a.social-share-twitter').click(function() {
          _gaq.push(['_trackEvent', 'Social Share Links', 'Twitter', nodePath]);
        });

        // Add analytics tracking to modal open.
        $(this).find('.modal-trigger a').click(function() {
          _gaq.push(['_trackEvent', 'ROC Modal', 'Open', nodePath]);
        });
      });

      // Add analytics tracking to modal close.
      $('#cboxClose').once('shareables-tracking', function() {
        $(this).click(function() {
          var nodePath = $('#cboxLoadedContent').find('article').attr('data-shareables-node-path');
          _gaq.push(['_trackEvent', 'ROC Modal', 'Close', nodePath]);
        });
      });

      // Make sure the analytics click behavior is attached to any new "boats" in the river of content.
      if (typeof(Drupal.NodeBroadcastSubscription) != 'undefined') {
        Drupal.NodeBroadcastSubscription.riverView.ev.on('render:done', function() {
          Drupal.behaviors.shareables.attach();
        });
      }
    }

  };

})(jQuery);
;
/**
 * @file
 * Javascript behaviors for the WHR Briefing Room Listings module.
 */

/**
 * Toggle function for Transcript Display on view.
 */
(function ($) {

  Drupal.behaviors.whr_briefing_room_listings = {

    attach: function (context, settings) {
      $('a.weekly-address-transcript-toggle', context).once('transcript-toggle',function(){
        $(this).click(function(event){
          event.preventDefault();
          if (!($('.views-field-field-transcript-1').hasClass('transcript-active'))) {
            $(this).text('Hide Transcript');
            $('.views-field-field-transcript-1').stop().slideDown().addClass('transcript-active');
          }
          else {
            $(this).text('Transcript');
            $('.views-field-field-transcript-1').stop().slideUp().removeClass('transcript-active');
          }
        })
      })

    }

  };

})(jQuery);;
